mapper.update:
update cms_article
set category_id = null
where category_id = #{categoryId}

mapper.select:
select ca.create_by, ca.create_date, ca.update_by, ca.update_date, ca.del_flag, ca.id, ca.category_id, cac.name as category_name, ca.tags, ca.title, ca.short_title, ca.description, ca.author, ca.thumbnail, ca.link_url, ca.praise_total, ca.view_total, ca.share_count, ca.read_count, ca.status, ca.allow_comment, ca.remarks, ca.sort from cms_article ca left join cms_category cac on ca.category_id=cac.id where ca.del_flag='0'
and ca.category_id = #{categoryId,jdbcType=BIGINT}
and ca.title like CONCAT('%', #{title}, '%')
and ca.status = #{status}
and EXISTS ( SELECT 1 FROM cms_article_tags cat, cms_tag ct WHERE cat.tag_id = ct.id AND ct.name = #{tag} AND ca.id = cat.article_id )
order by sort desc, create_date desc

mapper.update:
update cms_article
set read_count = read_count + 1
where id = #{id}
